import React from 'react';
import './MainPage.css'
import { useEffect, useState } from 'react';


function MainPage() {
    const [currentSearch, setCurrentSearch] = useState('')


    const getData = async () => {
        try {
          const data = await fetch("/");
          if (!data.ok) {
            throw new Error("Failed to fetch data");
          }
          const jsonData = await data.json();
          setItemsArray(jsonData);
          // console.log("Data fetched:", jsonData);
          // console.log(jsonData)

        } catch (error) {
          console.error("Error fetching data:", error);
        }
      }

    // event handler for the onsubmit event
    // when the form is submitted handleclick shoudld be called
    const handleClick = (e) => {
        // method is called on e,  to prevent the form from performing its default submit action, which is to reload the page.
        e.preventDefault();
        console.log(currentSearch)

        fetch('https://')
            .then(response => response.json())
            .then(data => console.log(data))
            .catch(error => console.error(error));







        setCurrentSearch('')
    }



    return (
      <div > 
      <header className='search'>
        <form className='bar' onSubmit={handleClick}> 
          {/* <label htmlFor="fname">First name:</label><br /> */}
          <input type="text" id="search" name="search" value={currentSearch} placeholder='Seach Here' onChange={(e) => setCurrentSearch(e.target.value)}
            /><br />
          <input type="submit" value="Submit" />
        </form> 
        </header>
      </div>
    );
  }

export default MainPage;


