import React from 'react';
import './MainPage.css'
import { useEffect, useState } from 'react';
import Movie from './MovieComponent';


function MainPage() {
    const [currentSearch, setCurrentSearch] = useState('');
    const [movieData , setMovieData] = useState([])

    const postData = async () => {
      const requestBody = currentSearch;
      console.log('line 13 RB', requestBody)
      try {
        const response = await fetch('http://localhost:4321/movielist', {
  method: 'POST', 
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify(requestBody)
});

if (!response.ok) {
  throw new Error(`HTTP error! Status: ${response.status}`);
}

const data = await response.json();
console.log('line 21 data', data);
setMovieData(data);
      } catch (error) {
        console.log('Error posting data:', error);
      }
    }

    const getData = () => {
        fetch('http://localhost:4321/movielist')

        .then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();  // Parses the body text as JSON
        })
        .then(data => {
          // console.log(data)
          setMovieData(data.results)

          // console.log('moviedata[0]', movieData[0].results[0].title)
        })
        .catch(error => {
          console.error('There was a problem with your fetch operation:', error);
        });
    }
    // event handler for the onsubmit event
    // when the form is submitted handleclick shoudld be called
    const handleClick = (e) => {
        // method is called on e,  to prevent the form from performing its default submit action, which is to reload the page.
        e.preventDefault();
        console.log(currentSearch)
        postData();  
        setCurrentSearch('')
    }

    useEffect(() => {
        getData();
    }, []);

  const movieDetails = movieData.map(movie => (
    <Movie key={movie.id} id={movie.id} title={movie.title} releaseDate={movie.release_date} image={movie.poster_path} rating={movie.vote_average} overview={movie.overview} />
  ));

    return (
      <div > 
      <header className='search'>
        <form className='bar' onSubmit={handleClick}> 
          <input type="text" id="search" name="search" value={currentSearch} placeholder='Seach Here' onChange={(e) => setCurrentSearch(e.target.value)}
            /><br />
          <input type="submit" value="Submit" />
        </form> 
        </header>
        <div>
          {movieDetails}
        </div>
      </div>
    );
  }

export default MainPage;


