  const [isHovering, setIsHovering] = useState(false)
  const [isClicked, setIsClicked] = useState(false)

  const trueRating = Math.round(rating * 10) / 10
  const year = releaseDate.slice(0, 4)

  console.log('line 13', isClicked)

  const handleMouseEnter = (e) => {
    setIsHovering(true)
  }

  const pictureClick = () => {
    console.log('picture clicked');
    setIsClicked(true);
    console.log('line 23', isClicked)
  }

  return (
    <div >
      <img id='poster' src={`https://image.tmdb.org/t/p/w500${image}`} height='500px' width='350px' alt="Italian Trulli" onMouseEnter={handleMouseEnter} onClick={ pictureClick()}/>
      {/* <div className='hover menu'>
      </div> */}
     <div>
     <div className='year-and-rating'> {year}
     <span className='rating'>{trueRating} </span>
      </div>
     <div className='title'>{title}</div>
      </div> 
    </div> 
  );
}

export default Movie;
